import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom"; // âœ… Import useParams
import "../App.css";

const NotificationList = () => {
  const { booking_id } = useParams(); // âœ… Extract booking ID from URL
  const [notification, setNotification] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!booking_id) return; // Ensure booking_id is present

    fetch(`http://localhost:5009/api/${booking_id}`) // âœ… Fetch specific notification
      .then((res) => {
        if (!res.ok) throw new Error("Notification not found");
        return res.json();
      })
      .then((data) => setNotification(data))
      .catch((err) => setError(err.message));
  }, [booking_id]);

  if (error) return <h2>Error: {error}</h2>;
  if (!notification) return <h2>Loading...</h2>;

  return (
    <div className="event-container">
      <div className="event-list">
        <h1>ðŸ“¬ Notification Details</h1>
        <div className="event-item">
          <p><strong>Booking ID:</strong> {notification.booking_id}</p>
          <p><strong>Email:</strong> {notification.user_email}</p>
          <p><strong>Event Name:</strong> {notification.event_id}</p>
          <p><strong>Status:</strong> {notification.status}</p>
          <p><strong>Notification Sent:</strong> {notification.notification_sent ? "Yes" : "No"}</p>
        </div>
      </div>
    </div>
  );
};

export default NotificationList;
