import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const NotificationDetail = () => {
  const { booking_id } = useParams(); // âœ… Get booking_id from URL
  const [notification, setNotification] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:5009/api/${booking_id}`) // âœ… Fetch specific notification
      .then((res) => res.json())
      .then((data) => {
        if (data.message) {
          setError(data.message); // Handle "Notification not found"
        } else {
          setNotification(data);
        }
      })
      .catch((err) => setError("Error fetching notification"));
  }, [booking_id]);

  if (error) return <p>Error: {error}</p>;
  if (!notification) return <p>Loading...</p>;

  return (
    <div>
      <h2>ðŸ“¬ Notification Details</h2>
      <p><strong>Booking ID:</strong> {notification.booking_id}</p>
      <p><strong>Email:</strong> {notification.user_email}</p>
      <p><strong>Status:</strong> {notification.status}</p>
      <p><strong>Notification Sent:</strong> {notification.notification_sent ? "Yes" : "No"}</p>
    </div>
  );
};

export default NotificationDetail;
